(7, 1, '2014-02-03 02:26:00', '2014-02-03 02:26:00', '<div style="text-align: justify; background: #e8ebf1; font-size: 25px; padding: 20px; width: 92%; line-height: 30px; border-left: 4px solid #30b7ff; border-bottom: 4px solid #30b7ff; margin-bottom: 10px;">\r\n\r\n<span style="color: #30b7ff; font-size: 30px;"><strong>2013</strong></span>\r\n<p style="padding-left: 60px;">5ème année d’ingénieur en informatique, spécialité en Ingénierie des Logiciels et de la Connaissance (ILC)</p>\r\n<p style="padding-left: 90px; font-size: 17px;"><em>l''Université de Technologie de Belfort-Montbéliard <a title="UTBM" href="http://www.utbm.fr" target="_blank">UTBM</a>.  </em></p>\r\n\r\n</div>\r\n<div style="text-align: justify; background: transparent; font-size: 25px; padding: 20px; width: 92%; line-height: 30px; border-left: 4px solid #30b7ff; border-bottom: 4px solid #30b7ff; margin-bottom: 10px;">\r\n\r\n<span style="color:#30b7ff; font-size: 30px;"><strong>2011</strong></span>\r\n<p style="padding-left: 60px;">Diplôme de licence d’Ingénieur des Travaux Informatiques (Analyste - Programmeur)</p>\r\n<p style="padding-left: 90px; font-size: 17px;"><em>Institut Africain d’Informatique <a title="IAI" href="http://www.iai-togo.com" target="_blank">IAI</a>.</em></p>\r\n\r\n</div>\r\n<div style="text-align: justify; background: #E8EBF1; font-size: 25px; padding: 20px; width: 92%; line-height: 30px; border-left: 4px solid #30b7ff; border-bottom: 4px solid #30b7ff; margin-bottom: 10px;">\r\n\r\n<span style="color: #30b7ff; font-size: 30px;"><strong>2007</strong></span>\r\n<p style="padding-left: 60px;">Baccalauréat Série D, (Mathématiques et Sciences de la nature)</p>\r\n<p style="padding-left: 90px; font-size: 17px;"><em>Lycée EMMAÜS</em></p>\r\n\r\n</div>', 'Formations et diplômes', '', 'publish', 'open', 'open', '', 'formations-et-diplomes', '', '', '2014-02-07 17:20:26', '2014-02-07 17:20:26', '', 0, 'http://localhost/wordpress/?page_id=7', 0, 'page', '', 0),
